	public void createDatabase() {
		try {
			try {
				databaseManager.createTable("drop table game");
			} catch (Exception e) {
				// TODO Auto-generated catch block
				e.printStackTrace(System.err);
			}
			try {
				databaseManager.createTable("drop table review");
			} catch (Exception e) {
				// TODO Auto-generated catch block
				e.printStackTrace(System.err);
			}
			try {
				databaseManager.createTable("create table game(id varchar(40), title varchar(100), summary varchar(200), image long varchar, rating integer)");
				databaseManager.createTable("create table review(id varchar(40), game_id varchar(40), summary varchar(200), rating integer, review varchar(300))");
			} catch (Exception e) {
				// TODO Auto-generated catch block
				e.printStackTrace(System.err);
			}
		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace(System.err);
		}
	}
	
