	public void saveGame(Game game) {
		//create table game(id varchar(32), title varchar(100), summary varchar(200), image varchar(1000), rating integer)");
		String SQL = "Insert into game (id, title, summary, image, rating) values (?,?,?,?,?)";
		List<Object> params = new ArrayList<Object>();
		params.add(game.getId().toString());
		params.add(game.getGameTitle());
		params.add(game.getGameSummary());
		params.add(game.getGameImage());
		params.add(game.getGameRating());
		
		System.out.println("Saving Game is "+ game.getGameTitle());
		databaseManager.updateQuery(SQL, params);
		
	}
	
	public List<Game> getGames() {//this will create the game with info specified and it add the game once it get the results
		List<Game> games = new ArrayList<>();
		try {
			String SQL = "select * from game";
			ResultSet result =databaseManager.getData(SQL);//get all games from data base
			while(result.next()) {
				Game game = new Game();//creates the game OBJECT
				game.setId(UUID.fromString(result.getString("id")));//pull all information and populate the game object for the all 5
				game.setGameTitle(result.getString("title"));
				game.setGameSummary(result.getString("summary"));
				game.setGameImage(result.getString("image"));
				game.setGameRating(result.getInt("rating"));
				game.setGameReview(reviewDAO.getReview(game.getId().toString()));
				
				System.out.println("Getting "+game.getGameTitle());
				
				games.add(game);//finally add the game to the list 
			}
		}
		catch(Exception e) {
			e.printStackTrace(System.err);
		}
		return games;
		
	}

